#include<iostream>
using namespace std;
class Solution {
    static constexpr int dirs[4][2] = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};
    public:
        int findMaxFish(vector<vector<int>> &grid) {
            int m = grid.size(), n = grid[0].size(), ans = 0;
            function<int(int, int)> dfs = [&](int x, int y) -> int {
                if (grid[x][y] < 0) // 标记成访问过
                    return 0;
                int sum = grid[x][y];
                grid[x][y] = -1; // 标记成访问过
                // 四方向移动
                for (auto &d: dirs) {
                    int xx = x + d[0];
                    int yy = y + d[1];
                    if (xx >= 0 && xx < m && yy >= 0 && yy < n)
                        sum += dfs(xx, yy);
                }
                return sum;
            };
            for (int i = 0; i < m; ++i)
                for (int j = 0; j < n; ++j)
                    ans = max(ans, dfs(i, j));
            return ans;
        }
};

int main() {
    Solution s;
    int m, n;
    cin >> m >> n;
    vector<vector<int>> grid(m, vector<int>(n));
    for (int i = 0; i < m; ++i) {
        for (int j = 0; j < n; ++j) {
            cin >> grid[i][j];
        }
    }
    cout << s.findMaxFish(grid) << endl;
}
