class Solution {
public:
    vector<int> getSubarrayBeauty(vector<int>& nums, int k, int x) {
        int sum[nums.size()];
        sum[0]=nums[0];
        for(int i=1;i<nums.size();i++) sum[i]=sum[i-1]+nums[i];
        int ans[nums.size()-k+1];
        for(int i=0;i<nums.size()-k+1;i++)
        {
            int count=0;
            for(int j=i;j<i+k;j++)
            {
                if(nums[j]<0) count++;
            }
            if(count>=x) ans[i]=sum[i+k-1]-sum[i];
            else ans[i]=0;
        }
        vector<int> v;
        for(int i=0;i<nums.size()-k+1;i++) v.push_back(ans[i]);
        return v;
    }
};
