class Solution {
public:
    int addMinimum(string word) {
        int n = word.size();
        vector<int> d(n + 1);
        for (int i = 1; i <= n; i++) {
            if (d[i - 1] > 0) {
                d[i] = d[i - 1] - 1;
            } else {
                if (word[i - 1] == 'a' && i > 1) {
                    d[i] = d[i - 2];
                } else if (word[i - 1] == 'c' && i > 2) {
                    if (word[i - 2] == 'b') {
                        d[i] = d[i - 3];
                    } else {
                        d[i] = d[i - 2] + 1;
                    }
                } else {
                    d[i] = i;
                }
            }
        }
        return d[n];
    }
};

