        vector<TreeNode*> q = {root};
        root->val = 0;
        while (!q.empty()) {
            vector<TreeNode*> q2;
            int sum = 0;
            for (auto fa : q) {
                if (fa->left) {
                    q2.push_back(fa->left);
                    fa->val += fa->left->val;
                }
                if (fa->right) {
                    q2.push_back(fa->right);
                    fa->val += fa->right->val;
                }
            }
            q = move(q2);
        }
        return root;
    }
};

