func findMatrix(nums []int) [][]int {
    m := make(map[int]int)
    for _,n := range nums{
        m[n]++
    }
    keys := []int{}
    for k,_ := range m{
        keys = append(keys,k)
    }
    sort.Ints(keys)
    ans := [][]int{}
    for _,k := range keys{
        for i:=0;i<m[k];i++{
            row := []int{}
            for _,t := range ans{
                if !find(t,k){
                    row = append(row,k)
                }
            }
            ans = append(ans,row)
        }
    }
    return ans
}

func find(nums []int,target int) bool{
    for _,n := range nums{
        if n == target{
            return true
        }
    }
    return false
}
