class Solution {
public:
    long long matrixSumQueries(int n, vector<vector<int>> &queries) {
        long long ans = 0;
        vector<long long> sums(n, 0);
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                sums[j] += i * j;
            }
        }
        for (int i = 0; i < queries.size(); i++) {
            int type = queries[i][0], index = queries[i][1], val = queries[i][2];
            if (type == 0) {
                ans -= sums[index] * val;
                for (int j = 0; j < n; j++) {
                    sums[j] += val;
                }
            } else {
                for (int j = 0; j < n; j++) {
                    ans -= sums[j] * val;
                    sums[j] += val * index;
                }
            }
        }
        return ans;
    }
};

