int solution(int a, int b) {
    int count = 0;
    int max = b;
    if(a<b){
        max = a;
    }
    for(int i = a;i<=b;i++){
        int divisorCount = 0;
        for(int j = 2;j<i;j++){
            if(i%j == 0){
                divisorCount++;
            }
        }
        if(divisorCount == 1){
            count++;
        }
    }
    return b - max - count +1;
}

