class Solution {public:
    int addMinimum(string word) {
        int n = word.size();
        vector<int> d(n + 1);
        for (int i = 1; i <= n; i++) {
            int add = 0;
            if (i == 1) add = 0;
            else if (word[i - 1] == 'a') {
                if (word[i - 2] == 'a') add = 1;
                else add = 0;
            }
            else if (word[i - 1] == 'b') {
                if (word[i - 2] == 'b' || word[i - 2] == 'c') add = 1;
                else add = 2;
            }
            else if (word[i - 1] == 'c') {
                if (word[i - 2] == 'c') add = 1;
                else add = 1;
            }
            if (add > d[i - 1]) add = d[i - 1];
            d[i] = add;
        }
        return d[n];
    }
};

