class Solution {
public:
    vector<long long> distance(vector<int>& nums) {
        int n = nums.size();
        unordered_map<int, vector<int>> groups;
        for (int i = 0; i < n; ++i) groups[nums[i]].push_back(i);
        // 相同元素分到同一组，记录下标
        vector<long long> ans(n);
        long long s[n + 1];
        s[0] = 0;
        for (auto& [_, a]: groups) {
            int m = a.size();
            for (int i = 0; i < m; ++i) s[i + 1] = s[i] + a[i];
            // 前缀和
            for (int i = 0; i < m; ++i) {
                long long target = a[i];
                long long right = s[m] - s[i] - target * (m - i);
                // 绿色面积
                ans[target] = right;
            }
        }
        return ans;
    }
};

/*

先进行排序, 之后使用二分查找找出在差值的区间, 二分查找过程中, 只要满足了 p 个区间, 就停止即可. 如果还没找到 p 个区间, 那么说明答案比当前小, 因此 right 左移. 如果找到了 p 个区间, 那么说明答案比当前大, 因此 left 右移.
*/
class Solution {
public:
    int minimizeMax(vector<int> &nums, int p) {
        sort(nums.begin(), nums.end());
        int left = -1, right = nums.back() - nums[0];
        while (left + 1 < right) {
            int cnt = 0, mid = (left + right) / 2, i = 0;
            for (int j = 1; j < nums.size(); ++j) {
                if (nums[j] - nums[i] >= mid) {
                    if (++cnt >= p) break;
                    i = j;
                }
            }
            (cnt >= p ? right : left) = mid;
        }
        return right;
    }
};

