        int m = grid.length;
        int n = grid[0].length;
        List<Integer> list = new ArrayList<Integer>();
        int xor = 0;
        for (int j = 0; j < m; j++) {
            int st = 0;
            for (int i = 0; i < n; i++) {
                st |= (grid[j][i] << i);
                xor |= st;
