class Solution {
    public int numberOfSubstrings(String s) {
        int res = 0;
        for (int i = 1; i < s.length(); ++i) {
            for (int len = 0; i + len < s.length(); ++len) {
                if (s.charAt(i + len) == '0') {
                    res++;
                }
                if (getSquare(len + 1) > getSquare(i + 1)) {
                    break;
                }
            }
        }
        return res;
    }

    int getSquare(int num) {
        return num * num;
    }
}

