class Solution {
     public static int sumCounts(int[] nums) {
        int n = nums.length;
        int[] sum = new int[n * 4];
        int[] todo = new int[n * 4];
        // 这个函数把 [l,r] 这个区间的值都加 add
        // add 可以等于 0，1，或 -1
        // 这个函数在执行之后返回
        // 这个区间内各元素的和之和
        void do(int o, int l, int r, int add) {
            sum[o] += add * (r - l + 1);
            todo[o] += add;
        }
        // o=1  [l,r] 1<=l<=r<=n
        // 把 [L,R] 加一，同时返回加一之前的区间和
        int query_and_add1(int o, int l, int r, int L, int R) {
            if (L <= l && r <= R) {
                // res 是加 1 之前的结果
                int res = sum[o];
                do(o, l, r, 1);
                return res;
            }
