class FenwickTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (self.n + 1)

    def __add(self, p, v):
        p += 1
        while p <= self.n:
            self.tree[p] += v
            p += p & (-p)

    def add(self, p, v):
        self.__add(p, v)

    def rsq(self, p):
        res = 0
        p += 1
        while p > 0:
            res += self.tree[p]
            p -= p & (-p)
        return res

class Solution:
    def numberOfAlternatingGroups(self, a: List[int], queries: List[List[int]]) -> List[int]:
        fenwick = FenwickTree(len(a))
        for i, val in enumerate(a):
            fenwick.add(i, val)

        ans = [None] * len(queries)
        sorted_list = SortedList(range(len(a)))

        def get_sum(s, e):
            s = sorted_list.bisect_left(s)
            e = sorted_list.bisect_left(e)
            res = fenwick.rsq(e) - fenwick.rsq(s)
            return res

        for i in range(len(queries)):
            op, x, y = queries[i]

            if op == 1:
                ans[i] = get_sum(x + 1, y)
            else:
                sorted_list.discard(x)
                sorted_list.add(x)
                fenwick.add(x, y - a[x])
                a[x] = y

        return ans
