        int n = a.size();
        vector<int> ans(n);
        long long p = 0, q = 0;
        for (int i = 0; i < n; ++i) {
            p |= 1LL << a[i];
            q |= 1LL << b[i];
            ans[i] = popcount(p & q);
        }
        return ans;
    }
