def shortestDistanceAfterQueries(n: int, queries: List[List[int]]) -> List[int]:
    g = [[i + 1] for i in range(n - 1)]
    vis = [-1] * (n - 1)

    def bfs(i: int) -> int:
        seen = [0] * n
        seen[n - 1] = 1
        q = deque([n - 1])
        while q:
            u = q.popleft()
            for v in g[u]:
                if seen[v] == 0:
                    seen[v] = seen[u] + 1
                    q.append(v)
        return seen[0]

    ans = [0] * len(queries)
    for i, (l, r) in enumerate(queries):
        g[l].append(r)
        ans[i] = bfs(i)
    return ans
