class FenwickTree:
    def __init__(self,arr):
        self.arr = arr
        self.tree = [0] * (len(self.arr) + 1)

    def fenwick_tree_build(self):
        for idx, num in enumerate(self.arr):
            self.update(idx, num)

    def update(self, idx, val):
        while idx < len(self.tree):
            self.tree[idx] += val
            idx += (idx & -idx)

    def query(self, idx):
        cnt = 0
        while idx > 0:
            cnt += self.tree[idx]
            idx -= (idx & -idx)
        return cnt

