import sys

class Solution:
    def sumCounts(self, nums: List[int]) -> int:
        n = len(nums)
        sum = [0] * (n * 4)
        todo = [0] * (n * 4)
        # 区间背包
        # o=1  [l,r] 1<=l<=r<=n
        # 把 [L,R] 加一，同时返回加一之前的区间和

        def do(o: int, l: int, r: int, add: int) -> None:
            sum[o] += add * (r - l + 1)
            todo[o] += add

        def push(o: int) -> None:
            left = o * 2
            right = o * 2 + 1
            todo[left], todo[right] = todo[o] // 2, todo[o] - todo[o] // 2
            sum[left] = sum[o] + sum[o] // 2
            sum[right] = sum[o] + sum[o] - sum[o] // 2
            todo[o] = 0

        def query_and_add1(o: int, L: int, R: int, l: int, r: int) -> int:
            if l <= L <= R <= r:
                return sum[o]
            mid = (L + R) // 2
            if todo[o]: push(o)
            if R <= mid: return query_and_add1(o * 2, L, R, l, r)
            if mid < L: return query_and_add1(o * 2 + 1, L, R, l, r)
            return query_and_add1(o * 2, L, mid, l, r) + query_and_add1(o * 2 + 1, mid + 1, R, l, r)

        # 初始化
        for i in range(n):
            sum[n + i] = 1
        # 上一个区间值
        last_val = -1
        ans = 0
        # 区间
        for r in range(1, n + 1):
            for l in range(1, r + 1):
                # 求区间前缀和
                query_and_add1(1, l, r, l, r)
                # 区间内数字范围唯一增加1
                if last_val != nums[r - 1]:
                    do(n + nums[r - 1], 1, n, 1)
                    # 计算上一个区间和
                    ans += sum[n + nums[r - 1]]
                last_val = nums[r - 1]
        ans *= ans
        return ans % (10 ** 9 + 7)
