class Solution:
    def smallestBeautifulString(self, s: str, k: int) -> str:
        seen, cnt = set(), 1
        seen.add(s)
        while cnt <= k:
            for s1 in seen:
                for i in range(len(s1) - 1):
                    s2 = s1[i + 1] + s1[:i] + s1[i + 1:]
                    if s2 not in seen:
                        cnt += 1
                        seen.add(s2)
                        if cnt == k:
                            return s2
        return ""

