class Solution {
public:
vector<int> getSubarrayBeauty(vector<int>& nums, int k, int x) {
    int n = nums.size(), maxv = nums[0], minv = nums[0];
    vector<int> ans;
    for (int i = k - 1; i < n; ++i) {
        if (i < n - 1) {
            if (maxv < nums[i + 1]) maxv = nums[i + 1];
            if (minv > nums[i + 1]) minv = nums[i + 1];
        }
        int cur_x = 0;
        for (int j = 0; j < k; ++j) {
            if (nums[i - j] <= minv) ++cur_x;
        }
        if (cur_x < x) ans.push_back(minv);
        else ans.push_back(0);
    }
    return ans;
}
};
