for (int i = 0; i < divisors.size(); i++) {
int tmp = 0;
for (int j = 0; j < nums.size(); j++) {
if (nums[j]%divisors[i] == 0) {
tmp++;
}
}
if (tmp > cnt || (tmp == cnt && divisors[i] < ans)) {
