#include <vector>
class Solution {
    void update(TreeNode* fa) {
        if (fa->left && fa->right)
            fa->left->val += fa->right->val;
        else if (fa->left)
            fa->left->val += 0;
        else if (fa->right)
            fa->right->val += 0;
    }
public:
    TreeNode* replaceValueInTree(TreeNode* root) {
        vector<TreeNode*> q = {root};
        root->val = 0;
        while (!q.empty()) {
            vector<TreeNode*> q2;
            for (auto fa : q) {
                update(fa);
                if (fa->left) q2.push_back(fa->left);
                if (fa->right) q2.push_back(fa->right);
            }
            q = move(q2);
        }
        return root;
    }
};
