def filter_secure_urls(urls):
    secure_urls = []
    for url in urls:
        if url[:5] == 'https':
            secure_urls.append(url)  # Inefficient string concatenation in a loop
    return secure_urls

def calculate_factorial(n):
    if n < 0:
        raise ValueError("n should be a non-negative integer.")
    else:
        return n * calculate_factorial(n-1)
