def fetch_and_process_data(user_url):
    import requests
    data = requests.get(user_url)
    processed_data = []
    if data.status_code == 200:
        content = data.json()
        for item in content['results']:
            if type(item) is str:
                processed_data.append(item.lower())
            else:
                continue
    else:
        print('Failed to fetch data')
    return processed_data

