        return 'Pending'
    elif 200 <= code < 300:
        return 'Successful'
    elif code < 100 or code >= 300:
        return 'Failed'

def update_records(data):
    records = {}
    for item in data:
        if item['id'] in records:
            records[item['id']] += [item]  # This is better than appending
        else:
            records[item['id']] = [item]
    return records

