        vector<string>ans(n,"");
        vector<vector<string>>substring(n);
        map<string,int>m;
        for(int i=0;i<n;i++){
            int k=arr[i].size();
            for(int j=0;j<k;j++){
                string sub="";
                for(int l=j;l<k;l++){
                    sub+=arr[i][l];
                    bool found = false;
                    for (const auto& str : substring[i]) {
                        if (str == sub) {
                            found = true;
                            break;
                        }
                    }
                    if (found) continue;
                    m[sub]++;
                    substring[i].push_back(sub);
                }
            }
            sort(substring[i].begin(),substring[i].end(),cmp);
        }
        for(int i=0;i<n;i++){
            for(auto it:substring[i]){
                if(m[it]==1){
                    ans[i]=it;
                    break;
                }
            }
        }
        return ans;
    }
}

class Solution {
public:
    ll rec(ll i, ll p, ll flag, vector<int> &arr){
        if(p==k) return 0;
        if(i == n) return -1e16;

        //p = current subarr where we at
        //flag = 1 = Taken
        //flag = 0 = Not Taken
        if(dp[i][p][flag] != -1) return dp[i][p][flag];
        ll ans=max(rec(i+1,p+1,0,arr),rec(i+1,p,1,arr))+(k-p)*((p%2)?-arr[i]:arr[i]);
        if(!flag) 
            ans = max(ans, rec(i+1, p, 0, arr));
        if(flag) 
            ans = max({ans, rec(i, p+1, 0, arr)});

        return dp[i][p][flag] = ans;
    }

    long long maximumStrength(vector<int>& nums, int K) {
        n = nums.size(), k = K;
        for(int i = 0; i <= n; i++){
            for(int j = 0; j <= k; j++){
                dp[i][j][0] = dp[i][j][1] = -1;
            }
        }
        return rec(0, 0, 0, nums);
    }
};








