using namespace std;

class Solution {
public:
    string minimizeStringValue(string s) {
        int n = s.size();
        string ans = "";
        int A[26] = {0};
        for (auto i : s) {
            if (i != '?') {
                A[i - 'a']++;
            }
        }
        string dum = "";
        for (int i = 0; i < n; i++) {
            if (s[i] == '?') {
                int min = 0, cnt = n + 1;
                for (int i = 0; i < 26; i++) {
                    if (A[i] < cnt) {
                        cnt = A[i];
                        min = i;
                    }
                }
                A[min]++;
                dum += (char)('a' + min);
            }
        }
        int j = 0;
        sort(dum.begin(), dum.end());
        for (int i = 0; i < n; i++) {
            if (s[i] == '?') {
                s[i] = dum[j];
                j++;
            }
            ans += s[i];
        }
        return ans;
    }
};
