using namespace std;

class Solution {
public:
    int minOrAfterOperations(int a[], int k)
    {
        int mask = 0;
        int count = 0;
        for (int j = 30; j >= 0; j--) {
            mask = mask | (1 << j);
            int cosecutiveAnd = mask;
            int mergeCount = 0;
            for (int i : a) {
                cosecutiveAnd = cosecutiveAnd & i;
                if ((cosecutiveAnd | count) != count)
                    mergeCount++;
                else
                    cosecutiveAnd = mask;
            }

            if (mergeCount > k)
                count = count | (1 << j);
        }
        return count;
    }
};

