int max_palin_len(string word, int *p)
{
	if(word.empty())
		return 0;

	int l = 0, r = word.size()-1;
	int max_palin_len = 0;

	while(l<r)
	{
		if(word[l] != word[r])
		{
			max_palin_len = max(max_palin_len, max(p[word[l]-'a'], p[word[r]-'a']));
			break;
		}

		l++, r--;
		max_palin_len++;
	}

	return max_palin_len;
}
int max_palin_len_all(string *words, int n)
{
	int max_palin_len = 0;
	for(int i=0; i<n; i++)
	{
		max_palin_len = max(max_palin_len, max_palin_len(words[i], p));
	}

	return max_palin_len;
}
