class Solution {
public:
    int findChampion(int n, vector<vector<int>>& edges) {
        int degree[n];
        memset(degree, 0, sizeof(degree));
        for(auto &e : edges) {
            degree[e[0]]++;
            degree[e[1]]++;
        }
        int champion = -1;
        for(int i = 0; i < n; i++) {
            if(degree[i] == 0) {
                if(champion == -1) {
                    champion = i;
                } else {
                    return -1;
                }
            }
        }
        return champion;
    }
};

