class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        def find(x: int) -> int:
            if fa[x] != x:
                fa[x] = find(fa[x])
            return fa[x]

        def union(x: int, y: int) -> None:
            x, y = find(x), find(y)
            if x != y:
                fa[y] = x

        def dis(x: int, y: int) -> int:
            x, y = find(x), find(y)
            if x == y:
