            if not in_circle(ox, oy, r, X, Y):
                return False
            for j in range(len(circles)):
                if j == i or vis[j]:
                    continue
                x1, y1, r1 = circles[j]
                if in_circle(ox, oy, r, x1, y1):
                    return False
                if in_circle(ox, oy, r, X, Y) and in_circle(x1, y1, r1, X, Y):
                    return False
                if in_circle(x1, y1, r1, ox, oy):
                    return False
            vis[i] = True
            return dfs(i + 1)
        return dfs(0)

