class Solution {
    public boolean canSortArray(int[] v) {
        int n = v.length;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n - 1; j++) {
                int k = v[j], c = 0;
                while (k > 0) {
                    k = k & (k - 1);
                    c++;
                }
                k = v[j + 1];
                int c1 = 0;
                while (k > 0) {
                    k = k & (k - 1);
                    c1++;
                }
                if (c == c1 && v[j] > v[j + 1])
                    swap(v, j, j + 1);
            }
        }
        for (int i = 1; i < n; i++) {
            if (v[i] < v[i - 1])
                return false;
        }
        return true;
    }

    private void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }
}

