    public List<Integer> beautifulIndices(String S, String A, String B, int k) {
        if (S.length() == 0 || A.length() > S.length() || B.length() > S.length()) return new ArrayList<Integer>();
        List<Integer> ans = new ArrayList<Integer>();
        List<Integer> a = getPatternMatchingIndex(S, A);
        List<Integer> b = getPatternMatchingIndex(S, B);
        int bIndex = 0, bL = b.size();
        for (int ai = 0; ai < a.size(); ai++) {
            while (bIndex < bL && b.get(bIndex) > a.get(ai) + k) bIndex++;
            if (bIndex < bL && Math.abs(b.get(bIndex) - a.get(ai)) <= k) ans.add(ai);
        }
        return ans;
    }
    public List<Integer> getPatternMatchingIndex(String s, String a) {
        if (a.length() > s.length()) return new ArrayList<Integer>();
        List<Integer> res = new ArrayList<Integer>();
        String s_ = a + "@" + s;
        List<Integer> lps = new ArrayList<Integer>();
        lps.add(0);
        for (int i = 1; i < s_.length(); i++) {
            int ind = lps.get(i - 1);
            while (ind > 0 && s_.charAt(ind) != s_.charAt(i)) ind = lps.get(ind - 1);
            lps.add((s_.charAt(ind) == s_.charAt(i)) ? ind + 1 : 0);
        }
        for (int i = 0; i < lps.size(); i++)
            if (lps.get(i) == a.length()) res.add(i - 2 * a.length());
        return res;
    }
}

