  public int minimumArrayLength(int[] v) {
    Arrays.sort(v);
    int n = v.length;
    int c = 1, g = Integer.MAX_VALUE;
    for (int i = 1; i < n; i++) {
      g = Math.min(g, gcd(v[i], v[i - 1]));
      if (g < v[0]) return 1;
      if (v[i] == v[0]) c++;
    }
    int k = (int) Math.ceil(c / (double) 2);
    return k;
  }
}

