public:
    int countMatchingSubarrays(vector<int>& nums, vector<int>& pattern) {
        int c = 0;
        int x = 1;
        int n = nums.size();
        int m = pattern.size();

        if(n == 0 || m == 0)
            return 0;
        int sum = 0;
        while(sum <= n - m){
            bool valid = true;
            int i = sum;
            for(int j = 0; j < m; j++){
                if(pattern[j] == 1){
                    if(nums[i + j + 1] <= nums[i + j]){
                        valid = false;
                        break;
                    }
                } else if(pattern[j] == 0){
                    if(nums[i + j + 1] != nums[i + j]){
                        valid = false;
                        break;
                    }
                } else if(pattern[j] == -1){
                    if(nums[i + j + 1] >= nums[i + j]){
                        valid = false;
                        break;
                    }
                }
            }
            if(valid){
                c++;
                x++;
            } else x = 1;
            sum += x;
        }
        return c;
    }
};
