class Solution {
    List<Integer> ans = new ArrayList<>();
    public List<Integer> preorder(Node root) {
        if(root == null)
            return ans;

        //base case
        ans.add(root.val);

        for(int i = 0; i < root.children.size(); i++){
            preorder(root.children.get(i));
        }
        return ans;
    }
}


