class Solution {
    public int semiOrderedPermutation(int[] nums) {
        int n = nums.length;
        int k = n - 1;
        int[] newNums = new int[n];
        for (int i = 0; i < n; i++) {
            newNums[i] = nums[i];
        }
        Arrays.sort(newNums);
        int ki = Arrays.asList(newNums).indexOf(1);
        int kj = Arrays.asList(newNums).lastIndexOf(n);
        if (ki > kj) {
            int ks = kj + n - 1 - ki;
            k = Math.min(k, ks);
        } else {
            int ks = ki + n - 1 - kj;
            k = Math.min(k, ks);
        }
        return k;
    }
}


