class Solution:
    def semiOrderedPermutation(self, nums: List[int]) -> int:

        if nums[0] == 1 and nums[len(nums) - 1] == len(nums):
            return 0

        op = 0
        min_idx = nums.index(min(nums))
        max_idx = nums.index(max(nums))
        if min_idx < max_idx:
            op = min_idx + (len(nums) - 1 - max_idx)
        if min_idx > max_idx:
            op = min_idx + (len(nums) - 1 - max_idx) - 1

        return op

class Solution:
    def specialPerm(self, nums: List[int]) -> int:
        n = len(nums)
        special_nums = defaultdict(list)
        special_nums[-1] = list(range(n))
        for i in range(len(nums)):
            for j in range(i+1, len(nums)):
                if nums[i] % nums[j] == 0 or nums[j] % nums[i] == 0:
                    special_nums[nums[i]].append(j)
                    special_nums[nums[j]].append(i)
        @cache
        def countPerm(mask, lastDigit):
            if mask == (2 ** n) - 1:
                return 1
            ans = 0
            for i in special_nums[lastDigit]:
                if mask & 1 << i:
                    continue
                ans += countPerm(mask | 1 << i, nums[i])
            return ans
        return countPerm(0, -1) % (10**9 + 7)

class Solution:
    def paintWalls(self, cost: List[int], time: List[int]) -> int:
        n = len(time)
        INF = 10**9+1
        dp = [[0 if i==0 else INF]*(n+1) for i in range(n+1)]
        for i in range(1, n+1):
            c0 = INF
            for j, (t, c) in enumerate(zip(time, cost), 1):
                i0 = max(i-t-1, 0)
                c1 = c + dp[i0][j-1]
                if c0 > c1:
                    c0 = c1
                dp[i][j] = c0
        return dp[-1][-1]

class Solution:
    def survivedRobotsHealths(self, positions: List[int], healths: List[int], directions: str) -> List[int]:
        # Sort based on position
        arr = []
        for i in range(len(positions)):
            arr.append([positions[i],healths[i],directions[i],i+1])
        arr.sort(key = lambda x: x[0])

        # Use Stack DS to emulate the scene
        right = [] # stores the right moving bots
        left = [] # stores the left moving bots

        for i in range(0, len(arr)):
            # same direction append to stack
            if arr[i][2] == 'R':
                right.append(arr[i])
            # different direction
            else:
                if right:
                    # pop the last element
                    bot1 = right.pop()
                    # compare the strenghts
                    if bot1[1] > arr[i][1]:
                        bot1[1] -= 1
                        right.append(bot1)

                    elif bot1[1] < arr[i][1]:
                        arr[i][1] -= 1
                        while right and right[-1][1] < arr[i][1]:
                            bot1 = right.pop()
                            arr[i][1] -= 1
                        if not right and arr[i][1] > 0:
                            left.append(arr[i])
                        elif right[-1][1] == arr[i][1]:
                            right.pop()
                        else:
                            right[-1][1] -= 1
                else:
                    left.append(arr[i])
        stack = left + right
        stack.sort(key = lambda x: x[-1])
        return [stack[i][1] for i in range(len(stack))]

class Solution:
    def findPrimePairs(self, n: int) -> List[List[int]]:
        prime=[0]*(n+1)
        prime[0]=1
        prime[1]=1
        for x in range(2,n+1):
            if prime[x]==0:
                i=x
                while x*i<=n:
                    if prime[x*i]==0:
                        prime[x*i]=1
                    i+=1
        result=[]
        for x in range(2,((n//2)+1)):
            if prime[x]==0 and prime[n-x]==0:
                result.append([x,n-x])
        return result

