class Solution {
public:
    int removeDuplicates(vector<int>& nums) {
        if(nums.empty()) return 0;
        int n = nums.size();
        int left = 0;
        for(int right = 1; right < n; right++){
            if(nums[left] != nums[right]) nums[++left] = nums[right];
        }
        return left+1;
    }
};

