class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        # 非递归并查集
        def find(x: int) -> int:
            if fa[x] != x:
                fa[x] = find(fa[x])
            return fa[x]

        def union(x: int, y: int):
            fa[find(x)] = find(y)

        res = [0]*len(queries)
        for i, (_, v) in enumerate(queries):
            u = v - 1
            if find(u) != find(v):
                union(u, v)
                res[i] += 2
            else:
                res[i] += 1
        return res

