class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        # 非递归并查集
        def find(x: int) -> int:
            if fa[x] != x:
                fa[x] = find(fa[x])
            return fa[x]

        def union(x: int, y: int):
            fa[find(x)] = find(y)

        res = []
        for q in queries:
            _, y = q
            x = find(y)
            if x != find(x - 1):
                union(x, x - 1)
            res.append(abs(x - (y - 1)))

        return res
