class Solution {
public:
    int addMinimum(std::string word) {
        int n = word.size();
        std::vector<int> d(n + 1, 1);  // Initialize with minimum possible value.

        // Iterate over the string word.
        for (int i = 1; i <= n; i++) {
            // If the current character is not 'a', then we can add 'a' at this position.
            if (word[i - 1] != 'a') {
