class Solution {
public:
    static constexpr int mod = 1e9 + 7;

    int sumDistance(vector<int>& nums, string s, int d) {
        int n = nums.size();
        vector<long long> pos(n);

        // Update the positions of the robots
        for (int i = 0; i < n; i++) {
            if (s[i] == 'L') {
                pos[i] = nums[i] - d;
            } else {
                pos[i] = nums[i] + d;
            }
        }

        // Sort the positions in ascending order
        sort(pos.begin(), pos.end());

        long long res = 0;
        // Update the sum of distances between all pairs of robots
        for (int i = 1; i < n; i++) {
            res += (pos[i] - pos[i-1]) * i;
            res %= mod;
        }

        return res;
    }
};

This solution has a time complexity of O(n log n), where n is the number of robots.

