        for (int step = 0; !q.empty(); ++step) {
            vector<int> nq;
            for (int i: q) {
                ans[i] = step;
                int mn = max(i - k + 1, k - i - 1);
                auto &s = sets[mn % 2];
                auto it = s.lower_bound(mn);
                int mx = min(i + k - 1, n * 2 - k - i);
                while (*it <= mx)
                    nq.push_back(*it), it = s.erase(it);
            }
            q = move(nq);
        }
        return ans;
    }
};
