        for (int i = 0; i < s.length(); ++i) {
            if (i >= indexOfFirstNonA && i < indexOfFirstA_AfterFirstNonA) {
                res.push_back(static_cast<char>(s[i] - 1));
            } else {
                res.push_back(s[i]);
            }
        }
        return res;
    }
};

