            for (int j = 1; j <= n; j++)
                w[i][j] = (i == j ? 0 : INF);
        //额外的捷径的权值是1。但不能是自身到自身，因为自身到自身的权值本来就是0.
        if (x != y) {
            w[x][y] = 1;
            w[y][x] = 1;
        }
        //建图，前后相邻 权值都是1。
        for (int i = 1; i < n; i++) {
            w[i][i + 1] = 1;
            w[i + 1][i] = 1;
        }
        //佛洛依德算法
        for (int p = 1; p <= n; p++)
            for (int st = 1; st <= n; st++)
