using namespace std;

class Solution {
public:
    vector<long long> findPrefixScore(vector<int>& nums) {
        vector<long long> ans;
        int tmp = nums[0];
        ans.push_back(nums[0] * 2);
        for (int i = 1; i < nums.size(); ++i) {
            if (nums[i] > tmp) {
                tmp = nums[i];
            }
            ans.push_back(nums[i] + tmp + ans[i - 1]);
        }
        return ans;
    }
};

int main() {
    vector<int> nums = {2, 3, 7, 5, 10};
    Solution solution;
    vector<long long> ans = solution.findPrefixScore(nums);
    for (auto num : ans) {
        cout << num << " ";
    }
    return 0;
