import java.util.*;

public class Solution {

    public List<Integer> numberOfAlternatingGroups(int[] a, int[][] queries) {
        int n = a.length;
        SortedSet<Integer> sl = new TreeSet<>();
        FenwickTree t = new FenwickTree(n);

        // op=1，添加一个结束位置 i
        // op=-1，移除一个结束位置 i
        void update(int i, int op) {
