class BIT {
public:
    BIT(int n) : tree(n) {}

    void inc(int i) {
        while (i < tree.size()) {
            ++tree[i];
            i += i & -i;
        }
    }

    int sum(int x) {
        int res = 0;
        while (x > 0) {
            res += tree[x];
            x &= x - 1;
        }
        return res;
    }

    int query(int left, int right) {
        return sum(right) - sum(left - 1);
    }

private:
    vector<int> tree;
};

class Solution {
public:
    long long countOperationsToEmptyArray(vector<int>& nums) {
        int n = nums.size();
        int id[n];
        iota(id, id + n, 0);
        sort(id, id + n, [&](int i, int j) {
            return nums[i] < nums[j];
        });
        long long ans = n;  // 先把 n 计入答案
        BIT t(n + 1);       // 下标从 1 开始
        int pre = 1;        // 上一个最小值的位置，初始为 1
        for (int k = 0; k < n; ++k) {
            int i = id[k] + 1;  // 下标从 1 开始
            if (i >= pre) {     // 从 pre 移动到 i，跳过已经删除的数
                ans += i - pre;
            } else {            // 从 pre 移动到 n，再从 1 移动到 i，跳过已经删除的数
                ans += n - pre + i;
            }
            t.inc(i);  // 删除 i
            pre = i;
        }
        return ans;
    }
};

