A: It's a simple fix, and you already had it, I just made the necessary modifications. The problem is that you are using an iterator to erase items, which makes the iteration invalid. The fix is to use a while loop instead, like so:
#include <unordered_map>
#include <vector>

class Solution
{
public:
    std::vector<std::vector<int>> findMatrix(std::vector<int> &nums)
    {
        std::unordered_map<int, int> cnt;
        for (int x : nums)
            ++cnt[x];

        std::vector<std::vector<int>> ans;
        while (!cnt.empty())
        {
            std::vector<int> row;
            auto it = cnt.begin();
            while (it != cnt.end())
            {
                row.push_back(it->first);
                if (--it->second == 0)
                    it = cnt.erase(it);
                else
                    ++it;
            }
            ans.push_back(row);
        }
        return ans;
    }
};

int main()
{
    std::vector<int> nums = {1, 3, 4, 1, 2, 3, 1};
    Solution s;
    s.findMatrix(nums);
    return 0;
}

class Solution {
public:
    int miceAndCheese(vector<int>& reward1, vector<int>& reward2, int k) {
        int ans = 0;
        int n = reward1.size();
        vector<int> diffs(n);
        for (int i = 0; i < n; i++) {
            diffs[i] = reward1[i] - reward2[i];
        }
        sort(diffs.begin(), diffs.end());
        for (int i = 1; i <= k; i++) {
            ans += reward2[i];
        }
        for (int i = 0; i < k; i++) {
            ans += diffs[n - i - 1];
        }
        return ans;
    }
};

